import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

const url_users = 'http://localhost:3001/users';

class App extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      regname: '',
      regaddress: ''
    };
  }
  componentDidMount() {
    this.getUser();
  }
  getUser() {
    axios
      .get(url_users)
      .then((results) => {
        this.setState({
          data: results.data
        });
      },)
      .catch((error) => {
        if (error.response) {
          // このリクエストはステータスコードとともに作成されます
          // 2xx系以外の時にエラーが発生します
          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          // このリクエストはレスポンスが返ってこない時に作成されます。
          // `error.request`はXMLHttpRequestのインスタンスです。
          console.log(error.request);
        } else {
          //それ以外で何か以上が起こった時
          console.log('Error', error.message);
        }
        console.log(error.config);
      });
  }
  registerUser(regname, regaddress) {
    const user = { name : regname, address : regaddress };
    axios.post(url_users, user).then(this.getUser.bind(this));
  }
  changeRegName(e) {
    this.setState({regname: e.target.value});
  }
  changeRegAddress(e) {
    this.setState({regaddress: e.target.value});
  }

  render() {
    return (
      <div className="App-header">
        <h1 className="app-title">ユーザ</h1>
        名前：<input value={this.state.regname} onChange={this.changeRegName.bind(this)} />{this.state.regname}
        連絡先：<input value={this.state.regaddress} onChange={this.changeRegAddress.bind(this)} />{this.state.regname}
        <input
          type="button"
          value="ユーザ登録"
          onClick={() => this.registerUser(this.state.regname, this.state.regaddress)}
        />
          {this.state.data.map((v)=>{
            return (
              <div className="user-list">
                <hr/>
                <p className="list-name"> 名前: {v.name} </p>
                <p className="list-address"> 連絡先: {v.address} </p>
              </div>
            );
          })}
      </div>
    );
  }
}

export default App;
