import React, { Component } from 'react';
import './ProfileBox.css';

class ProfileBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }
  deleteUser(id) {
    axios.delete(url_users + '/' + id).then(this.getUser.bind(this));
  }
  updateUser(id, name, address) {
    name = 'Edit';
    address = 'edit@gmail.com';
    const user = { name, address };
    axios.put(url_users + '/' + id, user).then(this.getUser.bind(this));
  }

  render() {
    return (
      <div className="user-list">
        <p className="list-name"> 名前: {props.name} </p>
        <p className="list-address"> 連絡先: {props.address} </p>
        <div>
          <button
            type="button"
            className="Button"
            onClick={() => this.updateUser(props.id, props.name, props.address)}
          >
            編集
          </button>
          <button
            type="button"
            className="Button"
            onClick={() => this.deleteUser(props.id)}
          >
            削除
          </button>
        </div>
      </div>
    );
  }
}

export default ProfileBox;
