import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

const url_users = 'http://localhost:3001/users';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      address: '',
    };
  }
  componentDidMount() {
    axios
      .get(url_users)
      .then((results) => {
        const data = results.data;
        const result = data.results[0];
        this.setState({
          name: result.name,
          address: result.address,
        });
      },
      )
      .catch((error) => {
        if (error.response) {
          // このリクエストはステータスコードとともに作成されます
          // 2xx系以外の時にエラーが発生します
          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          // このリクエストはレスポンスが返ってこない時に作成されます。
          // `error.request`はXMLHttpRequestのインスタンスです。
          console.log(error.request);
        } else {
          //それ以外で何か以上が起こった時
          console.log('Error', error.message);
        }
        console.log(error.config);
      });
  }

  render() {
    return (
      <div className="app">
        <h1 className="app-title">ユーザ</h1>
        <p> 名前: {this.state.name} </p>
        <p> 連絡先: {this.state.address}</p>
      </div>
    );
  }
}

export default App;
