import React, { Component } from 'react';
import axios from 'axios';
import './ProfileBox.css';

const url_users = 'http://localhost:3001/users';

class ProfileBox extends Component {
  constructor(props) {
    super(props);
    this.getUser = this.getUser.bind(this);
    this.setState({
      data: this.props.userdata,
    });
  }
  getUser() {
    axios
      .get(url_users)
      .then((results) => {
        this.setState({
          data: results.data
        });
      },)
      .catch((error) => {
        if (error.response) {
          // このリクエストはステータスコードとともに作成されます
          // 2xx系以外の時にエラーが発生します
          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          // このリクエストはレスポンスが返ってこない時に作成されます。
          // `error.request`はXMLHttpRequestのインスタンスです。
          console.log(error.request);
        } else {
          //それ以外で何か以上が起こった時
          console.log('Error', error.message);
        }
        console.log(error.config);
      });
  }
  deleteUser(id) {
    axios.delete(url_users + '/' + id).then(this.getUser.bind(this));
  }
  updateUser(id, name, address) {
    name = 'Edit';
    address = 'edit@gmail.com';
    const user = { name, address };
    axios.put(url_users + '/' + id, user).then(this.getUser.bind(this));
  }

  render() {
    return (
      <div className="user-list">
      <hr/>
        <p className="list-name"> 名前: {this.state.data.name} </p>
        <p className="list-address"> 連絡先: {this.state.data.address} </p>
        <div>
          <button
            type="button"
            className="Button"
            onClick={() => this.updateUser(this.state.data.id, this.state.data.name, this.state.data.address)}
          >
            編集
          </button>
          <button
            type="button"
            className="Button"
            onClick={() => this.deleteUser(this.state.data.id)}
          >
            削除
          </button>
        </div>
      </div>
    );
  }
}

export default ProfileBox;
